use fj::{
    core::{
        operations::{
            build::{BuildRegion, BuildSketch},
            sweep::SweepSketch,
            update::UpdateSketch,
        },
        topology::{Region, Sketch, Solid},
    },
    math::{Scalar, Vector},
};

pub fn model(size: impl Into<Vector<3>>, core: &mut fj::core::Core) -> Solid {
    let [x, y, z] = size.into().components;

    let bottom_surface = core.layers.topology.surfaces.xy_plane();
    let sweep_path = Vector::from([Scalar::ZERO, Scalar::ZERO, -z]);

    Sketch::empty(&core.layers.topology)
        .add_regions(
            [Region::polygon(
                [
                    [-x / 2., -y / 2.],
                    [x / 2., -y / 2.],
                    [x / 2., y / 2.],
                    [-x / 2., y / 2.],
                ],
                core.layers.topology.surfaces.space_2d(),
                core,
            )],
            core,
        )
        .sweep_sketch(bottom_surface, sweep_path, core)
}

use clap::Parser;

#[derive(Parser)]
struct Parameters {
    /// Size of the cuboid along the x-axis
    #[arg(long, default_value = "3.0")]
    x: f64,

    /// Size of the cuboid along the y-axis
    #[arg(long, default_value = "2.0")]
    y: f64,

    /// Size of the cuboid along the z-axis
    #[arg(long, default_value = "1.0")]
    z: f64,

    #[command(flatten)]
    fj: fj::Args,
}

fn main() -> fj::Result {
    let mut fj = fj::Instance::new();
    let params = Parameters::parse();

    let model = cuboid::model([params.x, params.y, params.z], &mut fj.core);
    fj.process_model_args(&model, params.fj)?;

    Ok(())
}

