pub fn model(size: impl Into<Vector<3>>, core: &mut fj::core::Core) -> Solid {
    let [x, y, z] = size.into().components;

    let bottom_surface = core.layers.topology.surfaces.xy_plane();
    let sweep_path = Vector::from([Scalar::ZERO, Scalar::ZERO, -z]);

    // Triangle circumscribed by a circle with 7.5mm radius
    // Vertices of equilateral triangle (one vertex pointing up)
    let r = 7.5;
    let sqrt3_2 = 0.866025403784; // √3/2
    let triangle_vertices = [
        [0.0, r],                    // top vertex
        [r * sqrt3_2, -r / 2.0],     // bottom-right vertex (clockwise winding for hole)
        [-r * sqrt3_2, -r / 2.0],    // bottom-left vertex
    ];

    Sketch::empty(&core.layers.topology)
        .add_regions(
            [
                // Outer rectangle (counter-clockwise)
                Region::polygon(
                    [
                        [-x / 2., -y / 2.],
                        [x / 2., -y / 2.],
                        [x / 2., y / 2.],
                        [-x / 2., y / 2.],
                    ],
                    core.layers.topology.surfaces.space_2d(),
                    core,
                ),
                // Inner triangle hole (clockwise winding)
                Region::polygon(
                    triangle_vertices,
                    core.layers.topology.surfaces.space_2d(),
                    core,
                ),
            ],
            core,
        )
        .sweep_sketch(bottom_surface, sweep_path, core)
}

